import React from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export default function GridItem({ produto }) {
    const navigate = useNavigate();

    const movimentar = () => {
        navigate(`/MovimentarProduto/${produto.id}`);
    };

    const excluirProduto = async () => {
        if (window.confirm("Tem certeza que deseja excluir este produto?")) {
            try {
                await axios.delete(`http://localhost:3001/produtos/${produto.id}`);
                alert("Produto excluído com sucesso!");
            } catch (error) {
                console.error("Erro ao excluir produto:", error);
                alert("Falha ao excluir o produto.");
            }
        }
    };

    return (
        <tr>
            <td style={{ width: '14.28%' }}>{produto.id}</td> {/* 100% / 7 colunas */}
            <td style={{ width: '14.28%' }}>{produto.nome}</td>
            <td style={{ width: '14.28%' }}>R${produto.preco}</td>
            <td style={{ width: '14.28%' }}>{produto.categoria}</td>
            <td style={{ width: '14.28%' }}>{produto.quantidade}</td>
            <td style={{ width: '28.56%' }}> {/* Dobro da largura para os botões */}
                <button className="btn bg-dark text-light me-2" onClick={movimentar}>Movimentar</button>
                <button className="btn btn-danger" onClick={excluirProduto}>Excluir</button>
            </td>
        </tr>
    );
}
