import { Link } from "react-router-dom";
import { useState } from "react";
import styles from "./sidebar.module.css";

export default function Sidebar() {
    const [isCadastroOpen, setIsCadastroOpen] = useState(false);

    return (
        <div className={`${styles.sidebar} d-flex flex-column vh-100 bg-dark text-light`}>
            <div className="p-3 text-center">
                <img
                    src="https://www.pa.senac.br/resources/site-v2/images/senac_logo_branco.png"
                    alt="Logo"
                    className="img-fluid md-2"
                    style={{ maxWidth: "120px" }}
                />
            </div>

            <ul className="nav flex-column">
                <li className="nav-item">
                    <Link to="/" className="nav-link text-light">
                        Home
                    </Link>
                </li>

                <li className="nav-item">
                    <Link to="/Grid" className="nav-link text-light">
                        Lista de Produtos
                    </Link>
                </li>

                <li className="nav-item">
                    <button
                        className="nav-link text-light bg-transparent border-0 text-start w-100"
                        onClick={() => setIsCadastroOpen(!isCadastroOpen)}
                    >
                        Cadastro {isCadastroOpen ? "▲" : "▼"}
                    </button>
                    {isCadastroOpen && (
                        <ul className="list-unstyled ms-3">
                            <li>
                                <Link to="/CadastroProduto" className="nav-link text-light">
                                    Produtos
                                </Link>
                            </li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
}
