        return <div>Carregando...</div>; // Exibe uma mensagem enquanto os produtos são carregados
    }

    if (error) {
        return <div>{error}</div>; // Exibe uma mensagem de erro se houver algum problema
    }

    return (
        <ul className={style.list}>
            {products.map(product => (
                <ProductItem key={product.id} product={product} />
            ))}
        </ul>
    );
}
