import axios from 'axios';
import GridItem from '../GridItem';
import { useState, useEffect } from 'react';
import Alerta from './Alerta'; // Importe o componente Alert

export default function Grid() {
    const [produto, setProduto] = useState([]);
    const [alert, setAlert] = useState(null); // Estado para o alerta

    useEffect(() => {
        const buscarProduto = async () => {
            try {
                const response = await axios.get('http://localhost:3001/produtos');
                setProduto(response.data);
            } catch (error) {
                console.error("Erro ao buscar produtos:", error);
                setAlert({ message: "Falha ao buscar produtos.", type: "danger" }); // Exibe alerta de erro
            }
        };

        buscarProduto();
    }, []);

    const fecharAlerta = () => {
        setAlert(null);
    };

    return (
        <div className="table-responsive">
            {alert && <Alerta message={alert.message} type={alert.type} onClose={fecharAlerta} />} {/* Exibe o alerta */}
            <div className='d-flex justify-content-center'>
                <h2 className='fw-bold'>
                    Produtos
                </h2>
            </div>
            <table className="table table-striped table-bordered mb-5">
                <thead>
                    <tr>
                        <th>Código EAN</th>
                        <th>Produto</th>
                        <th>Preço</th>
                        <th>Categoria</th>
                        <th>Quantidade</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {produto.map(p => (
                        <GridItem key={p.id} produto={p} />
                    ))}
                </tbody>
            </table>
        </div>
    );
}
