import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function ListaUsuarios() {
    const [usuarios, setUsuarios] = useState([]);
    const [usuarioParaExcluir, setUsuarioParaExcluir] = useState(null);
    const [modalAberto, setModalAberto] = useState(false);
    const [alerta, setAlerta] = useState(null);

    useEffect(() => {
        const buscarUsuarios = async () => {
            try {
                const response = await axios.get("http://localhost:3001/usuarios");
                setUsuarios(response.data);
            } catch (error) {
                console.error("Erro ao buscar usuários:", error);
                setAlerta({ message: "Falha ao buscar usuários.", type: "danger" });
            }
        };

        buscarUsuarios();
    }, []);

    const abrirModalExcluir = (usuario) => {
        setUsuarioParaExcluir(usuario);
        setModalAberto(true);
    };

    const fecharModalExcluir = () => {
        setUsuarioParaExcluir(null);
        setModalAberto(false);
    };

    const excluirUsuario = async () => {
        try {
            await axios.delete(`http://localhost:3001/usuarios/${usuarioParaExcluir.id}`);
            setAlerta({ message: "Usuário excluído com sucesso!", type: "success" });
            const response = await axios.get("http://localhost:3001/usuarios");
            setUsuarios(response.data);
            fecharModalExcluir();
        } catch (error) {
            console.error("Erro ao excluir usuário:", error);
            setAlerta({ message: "Falha ao excluir o usuário.", type: "danger" });
        }
    };

    const handleCloseAlerta = () => {
        setAlerta(null);
    };

    return (
        <div className="container mt-5 mb-5">
            {alerta && <Alerta message={alerta.message} type={alerta.type} onClose={handleCloseAlerta} />}
            <div className="row justify-content-center">
                <div className="col-md-10">
                    <div className="card shadow">
                        <div className="card-header bg-dark text-white text-center fw-bold">
                            <h3>Lista de Usuários</h3>
                        </div>
                        <div className="card-body">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Nome</th>
                                        <th>CEP</th>
                                        <th>Logradouro</th>
                                        <th>Bairro</th>
                                        <th>Cidade</th>
                                        <th>Estado</th>
                                        <th>Ações</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {usuarios.map((usuario) => (
                                        <tr key={usuario.id}>
                                            <td>{usuario.id}</td>
                                            <td>{usuario.usuario}</td>
                                            <td>{usuario.cep}</td>
                                            <td>{usuario.logradouro}</td>
                                            <td>{usuario.bairro}</td>
                                            <td>{usuario.cidade}</td>
                                            <td>{usuario.estado}</td>
                                            <td>
                                                <button
                                                    className="btn btn-danger btn-sm"
                                                    onClick={() => abrirModalExcluir(usuario)}
                                                >
                                                    Excluir
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
            {modalAberto && (
                <div className="modal show" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Excluir Usuário</h5>
                                <button type="button" className="btn-close" onClick={fecharModalExcluir}></button>
                            </div>
                            <div className="modal-body">
                                <p>Tem certeza que deseja excluir o usuário "{usuarioParaExcluir.usuario}"?</p>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" onClick={fecharModalExcluir}>Cancelar</button>
                                <button type="button" className="btn btn-danger" onClick={excluirUsuario}>Excluir</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
