import { useState, useEffect } from 'react';
import axios from 'axios';
import style from './productList.module.css';
import ProductItem from '../GridItem';

export default function ProductList() {
    const [products, setProducts] = useState([]); // Estado para armazenar os produtos
    const [loading, setLoading] = useState(true); // Estado para controlar o carregamento
    const [error, setError] = useState(null); // Estado para controlar o erro

    useEffect(() => {
        // Função para buscar os produtos
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:3001/produtos');
                setProducts(response.data); 
              
            } catch (error) {
                setError('Falha ao carregar os produtos.');
                setLoading(false);
            }
        };

        fetchProducts(); // Chama a função de buscar os produtos ao carregar o componente
    }, []); // O array vazio [] garante que a requisição seja feita apenas uma vez, quando o componente for montado

    if (loading) {
        return <div>Carregando...</div>; // Exibe uma mensagem enquanto os produtos são carregados
    }

    if (error) {
        return <div>{error}</div>; // Exibe uma mensagem de erro se houver algum problema
    }

    return (
        <ul className={style.list}>
            {products.map(product => (
                <ProductItem key={product.id} product={product} />
            ))}
        </ul>
    );
}
