import React, { useState} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";


export default function MovimentarProduto(){
    const [produto, setProduto] = useState([]);
    const [ProdutoSelecionado, setProdutoSelecionado] = useState("");
    const [quantidade, setQuantidade] = useState("");
    const [tipo, setTipo] = useState("entrada");
    const navigate = useNavigate();


    const buscarProduto = async () => {
        try {
            const response = await axios.get("http://localhost:3000/produtos");
            setProduto(response.data);
        } catch (error) {
            console.error("Erro ao buscar produtos:", error);
        }
    };
    buscarProduto();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!ProdutoSelecionado || !quantidade) {
            alert("Preencha todos os campos");
            return;
        }

        const movimento= {
            produto: ProdutoSelecionado,
            quantidade: parseInt(quantidade),
            tipo,
            data: new Date().toLocaleString()
        };

        try {
            await axios.post("http://localhost:3000/movimentacoes", movimento);
            alert("Movimentação registrada com sucesso!");
            setProdutoSelecionado("");
            setQuantidade("");
            setTipo("entrada");
        } catch (error) {
            console.error("Erro ao registrar movimentação:", error);
        }
    };

    const handleQuickMove = (produto) => {
        navigate(`/MovimentarProduto/${produto.id}`);
    };

    return (
        <div className="move-product-page">
            <h2>Movimentar Produto</h2>
            <form onSubmit={handleSubmit}>
                <label>Produto:</label>

                <label>Quantidade:</label>
                <input tipo="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} />

                <label>Tipo:</label>
                <select value={tipo} onChange={(e) => setTipo(e.target.value)}>
                    <option value="entrada">Entrada</option>
                    <option value="saida">Saída</option>
                </select>

                <button tipo="submit">Registrar Movimentação</button>
            </form>

            <h3>Produtos</h3>
       


        </div>
    );
}
