import { useState } from "react";

export default function Login({ onLogin }) {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleLogin = () => {
        if (!username || !password) {
            setError("Preencha todos os campos!");
            return;
        }

        // Simulação de autenticação (pode ser substituído por API futuramente)
        if (username === "admin" && password === "1234") {
            onLogin(); // Chama a função de login definida no AppRoutes.js
        } else {
            setError("Usuário ou senha incorretos!");
        }
    };

    return (
        <div className="d-flex justify-content-center align-items-center vh-100">
            <div className="card p-4 shadow w-50">
                <h4 className="text-center mb-3">Login</h4>

                {error && <div className="alert alert-danger">{error}</div>}

                <div className="mb-3">  
                    <label className="form-label fs-5">Usuário</label>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Digite seu usuário"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label fs-5">Senha</label>
                    <input
                        type="password"
                        className="form-control"
                        placeholder="Digite sua senha"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>

                <button
                    className="btn w-100 bg-dark text-light pt-3 pb-3 fs-5"
                    onClick={handleLogin}
                >
                    Entrar
                </button>
            </div>
        </div>
    );
}
