import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

export default function MovimentarProduto() {
    const [quantidade, setQuantidade] = useState("");
    const [tipo, setTipo] = useState("entrada");
    const [produto, setProduto] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const buscarProduto = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/produtos/${id}`);
                setProduto(response.data);
            } catch (error) {
                console.error("Erro ao buscar produto:", error);
            }
        };

        buscarProduto();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!produto || !quantidade) {
            alert("Preencha todos os campos");
            return;
        }

        const movimento = {
            produto: produto.nome,
            quantidade: parseInt(quantidade),
            tipo,
            data: new Date().toLocaleString(),
        };

        try {
            await axios.post("http://localhost:3001/movimentacoes", movimento);
            alert("Movimentação registrada com sucesso!");
            setQuantidade("");
            setTipo("entrada");
        } catch (error) {
            console.error("Erro ao registrar movimentação:", error);
        }
    };

    return (
        <div className="container mt-5 w-50">
            <div className="card border-secondary bg-dark text-white shadow-lg">
                <div className="card-header bg-secondary text-white">
                    <h3 className="text-center">Movimentar Produto</h3>
                </div>
                <div className="card-body">
                    {produto ? (
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label className="form-label fw-bold fs-2">{produto.nome}</label>
                            </div>

                            <div className="mb-3 w-50">
                                <label className="form-label">Quantidade:</label>
                                <input
                                    type="number"
                                    className="form-control border-secondary"
                                    value={quantidade}
                                    onChange={(e) => setQuantidade(e.target.value)}
                                    min="1"
                                />
                            </div>

                            <div className="mb-3 w-50">
                                <label className="form-label ">Tipo:</label>
                                <select
                                    className="form-select border-secondary"
                                    value={tipo}
                                    onChange={(e) => setTipo(e.target.value)}
                                >
                                    <option value="entrada">Entrada</option>
                                    <option value="saida">Saída</option>
                                </select>
                            </div>

                            <div className="d-grid w-25 text">
                                <button type="submit" className="btn bg-white">
                                    Registrar Movimentação
                                </button>
                            </div>
                        </form>
                    ) : (
                        <div className="text-center">
                            <div className="spinner-border text-light" role="status">
                                <span className="visually-hidden">Carregando...</span>
                            </div>
                            <p className="mt-2">Buscando produto...</p>
                        </div>
                )}
                </div>
            </div>
        </div>
    );
}
