import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export default function GridItem({ produto }) {
    const navigate = useNavigate();
    const [ativo, setAtivo] = useState(true); // Inicialmente, o produto está ativo

    const movimentar = () => {
        navigate(`/MovimentarProduto/${produto.id}`);
    };

    const desativarProduto = async () => {
        try {
            await axios.put(`http://localhost:3001/produtos/${produto.id}`, {
                ...produto,
                ativo: !ativo,
            });
            setAtivo(!ativo);
        } catch (error) {
            console.error("Erro ao desativar produto:", error);
            alert("Falha ao desativar o produto.");
        }
    };

    return (
        <tr>
            <td>{produto.id}</td>
            <td>{produto.nome}</td>
            <td>R${produto.preco}</td>
            <td>{produto.categoria}</td>
            <td>{produto.quantidade}</td>
            <td>
                <button className="btn bg-dark text-light" onClick={movimentar}>Movimentar</button>
            </td>
            <td>
                <button className="btn btn-danger" onClick={desativarProduto}>
                    {ativo ? "Desativar" : "Ativar"}
                </button>
            </td>
        </tr>
    );
}
