import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

export default function ListaMovimentacoes() {
    const [movimentacoes, setMovimentacoes] = useState([]);

    useEffect(() => {
        const buscarMovimentacoes = async () => {
            try {
                const response = await axios.get("http://localhost:3001/movimentacoes");
                setMovimentacoes(response.data.reverse()); // Inverte a ordem dos dados
            } catch (error) {
                console.error("Erro ao buscar movimentações:", error);
            }
        };

        buscarMovimentacoes();
    }, []);

    return (
        <div className="container mt-5 mb-5 vh-100">
            <div className="row justify-content-center">
                <div className="col-md-8">
                    <div className="card shadow">
                        <div className="card-header bg-dark text-white text-center">
                            <h3>Movimentações</h3>
                        </div>
                        <div className="card-body">
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered">
                                    <thead>
                                        <tr>
                                            <th>Produto</th>
                                            <th>Quantidade</th>
                                            <th>Tipo</th>
                                            <th>Data</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {movimentacoes.map((movimentacao) => (
                                            <tr key={movimentacao.id}>
                                                <td>{movimentacao.produto}</td>
                                                <td>{movimentacao.quantidade}</td>
                                                <td>{movimentacao.tipo}</td>
                                                <td>{movimentacao.data}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
